"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[6831],{8904:e=>{e.exports=JSON.parse('{"label":"biology","permalink":"/tags/biology","allTagsPath":"/tags","count":17,"items":[{"id":"learning/biology/bioinformatics","title":"Bioinformatics","description":"Genomics \u662f\u6307\u7814\u7a76\u751f\u7269\u67d3\u8272\u9ad4\u7684\u7d50\u69cb\u548c\u529f\u80fd\u3002\u5c0d\u65bc\u751f\u7269\u67d3\u8272\u9ad4\u7684\u7d50\u69cb\u4f86\u8aaa\uff0c\u4eba\u9ad4\u5927\u6982\u6709 3 \u5104\u500b\u6838\u82f7\u9178 (A\u3001C\u3001G\u3001T) \u7d44\u6210\u57fa\u56e0\u7d44\uff0c\u800c\u57fa\u56e0\u7d44\u53c8\u88ab\u5206\u6210 23 \u500b\u67d3\u8272\u9ad4\uff0822 \u500b\u666e\u904d\u7684 + XX \u6216 XY\uff09\uff0c\u800c\u6bcf\u500b\u67d3\u8272\u9ad4\u53c8\u6709\u7740 centromere \u548c telomere\u3002\u5c0d\u65bc\u57fa\u56e0\u7d44\u7684\u529f\u80fd\u4f86\u8aaa\uff0c\u5b83\u5305\u542b\u4e86\u6240\u6709\u8eab\u9ad4\u6703\u4f7f\u7528\u5230\u7684\u6771\u897f\uff0c\u4f8b\u5982\u547c\u5438\u4ee3\u8b1d\u3001\u5927\u8166\u7b49\u7b49\u3002","permalink":"/learning/biology/bioinformatics"},{"id":"learning/biology/index","title":"Biology","description":"To know about Biology, it\'s important to have a good understanding of General Science, Chemistry, Physics, Statistics, and Math.","permalink":"/learning/biology/"},{"id":"learning/biology/cell-division","title":"Cell Division","description":"Cell division \u662f\u6307\u7d30\u80de\u5206\u88c2\u7684\u904e\u7a0b\uff0c\u662f\u751f\u7269\u9ad4\u7e41\u6b96\u7684\u57fa\u790e\u3002\u5b83\u662f\u4e00\u500b\u5faa\u74b0\u6027\u7684\u6b65\u9a5f\uff0c\u7d30\u80de\u6703\u5148\u9032\u884c DNA \u8907\u88fd\uff0c\u63a5\u8457\u7d30\u80de\u8cea\u6703\u5206\u88c2\uff0c\u6700\u5f8c\u7d30\u80de\u6703\u5206\u88c2\u51fa\u5169\u500b\u65b0\u7684\u7d30\u80de\uff0c\u5b8c\u6210 cell division \u7684\u904e\u7a0b\u3002","permalink":"/learning/biology/cell-division"},{"id":"learning/biology/cell-signaling","title":"Cell Signaling","description":"Cell signaling \u662f\u4e00\u7a2e\u7d30\u80de\u9593\u7684\u6e9d\u901a\u65b9\u5f0f\uff0c\u53ef\u4ee5\u767c\u5c04\uff0c\u63a5\u6536\u548c\u8655\u7406\u8a0a\u606f\u3002\u5b83\u53ef\u4ee5\u5206\u70ba\u56db\u7a2e\uff1aParacrine signaling\uff0cAutocrine signaling\uff0cEndocrine signaling \u548c Direct contact\u3002Paracrine signaling \u662f\u8ddd\u96e2\u8f03\u8fd1\u7684\u50b3\u905e\uff0cNeurotransmitters \u5728 nerve cells \u4e4b\u9593\u50b3\u905e\u8a0a\u606f\uff0cAutocrine signaling \u662f\u5c07 ligands \u50b3\u905e\u5230\u81ea\u5df1\u8eab\u4e0a\u7684 receptors\uff0cEndocrine signaling \u5c07 signals \u4ee5 bloodstream \u7684\u65b9\u5f0f\u5206\u5e03\u5230\u8eab\u9ad4\u7684\u4e0d\u540c\u90e8\u4f4d\uff0cDirect contact \u5247\u662f\u76f4\u63a5\u63a5\u89f8\u7684\u50b3\u905e\u65b9\u5f0f\u3002","permalink":"/learning/biology/cell-signaling"},{"id":"learning/biology/cellular-respiration","title":"Cellular Respiration","description":"Cellular respiration \u662f\u6307\u7d30\u80de\u5982\u4f55\u5c07\u5927\u7684\u5206\u5b50\u5206\u89e3\u6210\u4e00\u5c0f\u4efd\u4e00\u5c0f\u4efd\uff0c\u4ee5\u53ca\u6700\u5f8c\u5c07\u5206\u89e3\u51fa\u4f86\u7684 energy \u5132\u5b58\u70ba ATP \u7684\u904e\u7a0b\u3002\u7d30\u80de\u5206\u89e3 glucose \u7684\u904e\u7a0b\u4e2d\uff0c\u5fc5\u9808\u6703\u9032\u884c catabolic reactions\uff0c\u4e26\u4e14\u5f9e\u9ad8\u80fd\u91cf\u7684\u74b0\u5883\u8f49\u8b8a\u6210\u4f4e\u80fd\u91cf\u7684\u74b0\u5883\uff0c\u6700\u5f8c\u5c07 energy \u91cb\u653e\u51fa\u4f86\uff0c\u5176\u4e2d\u4e00\u90e8\u5206\u88fd\u9020\u51fa ATP \u4ee5\u53ca\u53e6\u4e00\u90e8\u5206\u5247\u6d88\u6563\u70ba heat\u3002\u5728\u6574\u500b\u904e\u7a0b\u4e2d\uff0celectron carriers \u5c31\u8ca0\u8cac\u5c07 electrons \u5f9e\u4e00\u500b\u7269\u8cea\u8f49\u79fb\u5230\u53e6\u4e00\u500b\u7269\u8cea\uff0c\u5e36\u52d5\u6574\u500b\u7d30\u80de\u547c\u5438\u7684\u904e\u7a0b\u3002","permalink":"/learning/biology/cellular-respiration"},{"id":"learning/biology/central-dogma","title":"Central Dogma","description":"Central dogma \u662f\u4e00\u500b\u6982\u5ff5\uff0c\u6307 DNA \u900f\u904e\u8f49\u9304\u3001\u7ffb\u8b6f\u7684\u6b65\u9a5f\u5c07 genetic information \u8f49\u63db\u70ba\u86cb\u767d\u8cea\u7684\u904e\u7a0b\u3002\u8f49\u9304\u662f\u6307 DNA sequence \u6703\u88ab\\"\u8f49\u9304\\"\u6210\u76f8\u4f3c\u7684 RNA sequence\uff0c\u7ffb\u8b6f\u5247\u662f\u6307 mRNA \u88ab\\"\u7ffb\u8b6f\\"\u6210\u5c0d\u61c9\u7684 amino acids \\"\u8a9e\u8a00\\"\uff0c\u4ee5\u6b64\u5efa\u69cb polypeptide\u3002\u6b64\u5916\uff0ccodon \u548c amino acid \u4e4b\u9593\u7684\u5c0d\u61c9\u95dc\u4fc2\u53eb\u505a genetic code\uff0c\u53ef\u4ee5\u7528\u5b8c\u6574\u7684 genetic code table \u4f86\u8868\u793a\uff0c\u4e26\u6709 start codon \u4ee5\u53ca stop codon \u4f86\u6a19\u8a3b\u958b\u59cb\u8207\u7d50\u675f\u7684\u4f4d\u7f6e\u3002","permalink":"/learning/biology/central-dogma"},{"id":"learning/biology/chemistry-of-life","title":"Chemistry of Life","description":"\u7d50\u69cb\u751f\u7269\u9ad4\u7684\u5206\u5b50\uff0c\u901a\u5e38\u7531\u4e0d\u540c\u7a2e\u985e\u7684\u539f\u5b50\u900f\u904e\u5316\u5b78\u9375\u4f86\u7d50\u5408\u800c\u6210\u3002\u539f\u5b50\u9593\u53ef\u80fd\u6703\u7522\u751f\u5404\u7a2e\u5f62\u5f0f\u7684\u5316\u5b78\u9375\uff0c\u4f8b\u5982\u5171\u50f9\u9375\u3001\u6975\u6027\u5171\u50f9\u9375\u3001\u6c2b\u9375\u7b49\uff0c\u9019\u4e9b\u9375\u6703\u7a69\u5b9a\u86cb\u767d\u8cea\u8207 DNA \u67b6\u69cb\uff0c\u548c\u5f71\u97ff\u5404\u7a2e\u5316\u5b78\u53cd\u61c9\u5e73\u8861\u3002","permalink":"/learning/biology/chemistry-of-life"},{"id":"learning/biology/classical-and-molecular-genetics","title":"Classical and Molecular Genetics","description":"Mendelian genetics \u6307\u7684\u662f\u7531 Gregor Mendel \u6240\u767c\u73fe\u7684\u907a\u50b3\u5b9a\u5f8b\uff0c\u4e5f\u5c31\u662f\u900f\u904e\u7236\u6bcd\u89aa genes \u7684\u5f71\u97ff\uff0c\u63a7\u5236\u4eba\u985e\u7279\u5fb5 (\u9aee\u8272\u3001\u8eab\u9ad8\u7b49) \u7684\u539f\u56e0\u3002Mendel \u4f7f\u7528 pure-breeding \u7684 pea plants \u4f5c\u70ba\u7814\u7a76\u5c0d\u8c61\uff0c\u6700\u7d42\u767c\u73fe\u4e86\u5169\u5927\u5b9a\u7406\uff1aThe law of segregation \u548c The law of independent assortment\uff0c\u4ee5\u53ca\u63d0\u51fa\u4e86 Mendel\'s model of inheritance\u3002","permalink":"/learning/biology/classical-and-molecular-genetics"},{"id":"learning/biology/dna-as-the-genetic-material","title":"DNA as the Genetic Material","description":"DNA \u662f\u4e00\u7a2e\u4fdd\u5b58\u907a\u50b3\u4fe1\u606f\u7684\u5206\u5b50\uff0c\u5b83\u7531\u812b\u6c27\u6838\u82f7\u9178\u69cb\u6210\uff0c\u6bcf\u500b\u6838\u82f7\u9178\u90fd\u7531\u4e00\u500b\u78b3\u539f\u5b50\u3001\u4e00\u500b\u6c2e\u539f\u5b50\u548c\u4e00\u500b\u6c2b\u539f\u5b50\u7d44\u6210\u3002DNA \u5177\u6709\u7279\u5b9a\u7684\u7d50\u69cb\uff0c\u53ef\u4ee5\u88ab\u8b80\u53d6\u800c\u7522\u751f\u86cb\u767d\u8cea\u3002\u86cb\u767d\u8cea\u662f\u7d30\u80de\u7684\u57fa\u672c\u7d44\u6210\u5143\u7d20\uff0c\u53c3\u8207\u7d30\u80de\u7684\u751f\u9577\u548c\u6b63\u5e38\u7684\u529f\u80fd\uff0c\u4f8b\u5982\u4ee3\u8b1d\u548c\u4fe1\u865f\u50b3\u5c0e\u3002\u56e0\u6b64\uff0cDNA \u5c0d\u65bc\u6240\u6709\u7684\u751f\u7269\u90fd\u81f3\u95dc\u91cd\u8981\u3002","permalink":"/learning/biology/dna-as-the-genetic-material"},{"id":"learning/biology/energy-and-enzymes","title":"Energy and Enzymes","description":"Metabolism \u662f\u6307\u7d30\u80de\u4e2d\u4e00\u9023\u4e32\u7684 chemical reaction\uff0c\u5b83\u5011\u53ef\u4ee5\u7528\u4f86\u5132\u5b58\u3001\u91cb\u653e\u3001\u8f49\u63db energy\uff0c\u4e26\u4e14\u53ef\u4ee5\u5efa\u69cb\u548c\u7834\u58de\u8907\u96dc\u7684 molecules\uff0c\u4f8b\u5982 sugar, proteins, DNA \u7b49\u7b49\u3002\u9019\u4e9b chemical reaction \u90fd\u9700\u8981\u4e00\u7a2e protein \u7a31\u4f5c enzyme \u4f86 facilitate \u548c catalyze\uff0c\u4ee5\u4fbf\u6b63\u78ba\u7684\u767c\u751f\u9019\u4e9b reaction\u3002\u4f8b\u5982\uff0ccellular respiration \u4f7f\u7528 enzyme \u4f86\u5c07 glucose \u5206\u89e3\u6210 ATP\uff0c\u800c photosynthesis \u5247\u662f\u628a\u592a\u967d\u7684\u80fd\u91cf\u8f49\u63db\u6210 glucose\u3002","permalink":"/learning/biology/energy-and-enzymes"},{"id":"learning/biology/gene-regulation","title":"Gene Regulation","description":"Gene Regulation \u662f\u6307\u7d30\u80de\u5982\u4f55\u6c7a\u5b9a\u5728\u4ec0\u9ebc\u6642\u9593\u3001\u4ec0\u9ebc\u5730\u9ede\u555f\u52d5\u54ea\u4e9b genes\uff0c\u4ee5\u4fbf\u7d30\u80de\u80fd\u5920\u591a\u6a23\u5316\u7684\u57f7\u884c\u4e0d\u540c\u7684\u4e8b\u60c5\u3002Gene Regulation \u4e5f\u6703\u5f71\u97ff\u5230 gene expression \u7684\u6240\u6709\u968e\u6bb5\uff0c\u4f8b\u5982 chromatin accessibility\u3001transcription\u3001RNA processing\u3001RNA stability\u3001translation \u4ee5\u53ca protein activity \u7b49\u3002","permalink":"/learning/biology/gene-regulation"},{"id":"learning/biology/macromolecules","title":"Macromolecules","description":"Macromolecules \u662f\u4e00\u7a2e\u5927\u5206\u5b50\uff0c\u901a\u5e38\u7531\u5f88\u591a\u5c0f\u55ae\u4f4d\u91cd\u8907\u7684\u7d44\u6210\uff0c\u9019\u4e9b\u5c0f\u55ae\u4f4d\u53eb\u4f5c monomers\u3002\u9019\u4e9b macromolecules \u5927\u591a\u662f\u7528 dehydration synthesis reactions \u4f86\u7d44\u5408\u800c\u6210\uff0c\u800c\u6709\u6642\u6703\u9700\u8981 hydrolysis reaction \u4f86\u62c6\u89e3\u3002\u5e38\u898b\u7684 macromolecules \u5305\u62ec carbohydrates\u3001lipids\u3001nucleic acids \u548c proteins\uff0c\u4e14\u6bcf\u500b macromolecule \u90fd\u6709\u63d0\u4f9b\u4e0d\u540c\u529f\u80fd\u7684\u7279\u6027\u8207\u7d50\u69cb\u3002","permalink":"/learning/biology/macromolecules"},{"id":"learning/biology/membrane-transport","title":"Membrane Transport","description":"Plasma membrane \u662f\u6bcf\u500b\u7d30\u80de\u7684\u5916\u5c64\u4fdd\u8b77\u819c\uff0c\u5b83\u5305\u542b\u8a31\u591a\u5143\u7d20\uff0c\u5982 phospholipids, cholesterol, proteins \u548c carbohydrate groups\uff0c\u4e26\u4e14\u662f\u4ee5 fluid mosaic model \u7684\u67b6\u69cb\u4f86\u5b9a\u7fa9\u3002Diffusion \u662f\u6307\u9ad8\u6fc3\u5ea6\u7684\u5206\u5b50\u6703\u4e0d\u65b7\u7684\u5f9e\u9ad8\u6fc3\u5ea6\u7684\u5340\u57df\u79fb\u52d5\u5230\u4f4e\u6fc3\u5ea6\u7684\u5340\u57df\uff0c\u4ee5\u9054\u5230\u5e73\u8861\u7684\u72c0\u614b\uff0c\u800c Osmosis \u5247\u662f\u5728\u4e0d\u540c\u6fc3\u5ea6\u7684\u5169\u500b\u5340\u57df\u4e2d\u9593\u52a0\u5165\u4e00\u500b semipermeable membrane \u7684\u60c5\u6cc1\u4e0b\uff0c\u6c34\u5206\u5b50\u6703\u7531\u4f4e\u6fc3\u5ea6\u5340\u57df\u6d41\u5411\u9ad8\u6fc3\u5ea6\u5340\u57df\uff0c\u7a31\u70ba Tonicity\u3002Membrane transport \u662f\u6307\u7d30\u80de\u819c\u4e0a\u7684\u8f09\u9ad4\u5206\u5b50\u5229\u7528\u80fd\u91cf\u7a7f\u904e\u819c\u7684\u6a5f\u5236\u4f86\u5c07\u7269\u8cea\u5f9e\u4e00\u500b\u5340\u57df\u8f38\u9001\u5230\u53e6\u4e00\u500b\u5340\u57df\u7684\u904e\u7a0b\u3002","permalink":"/learning/biology/membrane-transport"},{"id":"learning/biology/properties-of-carbon","title":"Properties of Carbon","description":"Carbon \u662f\u4e00\u7a2e\u5ee3\u6cdb\u51fa\u73fe\u5728\u81ea\u7136\u754c\u7684\u5143\u7d20\uff0c\u53ef\u4ee5\u7528\u65bc\u5275\u9020\u8907\u96dc\u7684 macromolecules\uff0c\u5982 DNA, RNA, sugars, fats \u7b49\u3002\u53ea\u8981\u662f\u7531 carbon \u548c hydrogen \u7d44\u6210\u7684 organic molecules \u90fd\u7a31\u4f5c hydrocarbon\uff0c\u4ed6\u5011\u7684 covalent bonds \u53ef\u4ee5\u5132\u5b58\u5927\u91cf\u7684 energy\uff0c\u4f7f\u5f97 hydrocarbons \u6210\u70ba\u4e00\u7a2e\u7406\u60f3\u7684\u71c3\u6599\u4f86\u6e90\u3002Isomer \u5247\u662f\u6307 molecular formula \u76f8\u540c\uff0c\u4f46\u67b6\u69cb\u4e0d\u540c\u7684 molecules\uff0c\u53ef\u4ee5\u5206\u6210 structural isomers \u548c stereoisomers \u5169\u7a2e\u3002","permalink":"/learning/biology/properties-of-carbon"},{"id":"learning/biology/scientific-method","title":"Scientific Method","description":"Scientific method \u662f\u5404\u7a2e\u81ea\u7136\u79d1\u5b78\u7684\u6839\u672c\uff0c\u7528 5 + 1 \u500b\u6b65\u9a5f\u4f86\u89e3\u6c7a\u554f\u984c\u3002","permalink":"/learning/biology/scientific-method"},{"id":"learning/biology/structure-of-a-cell","title":"Structure of a Cell","description":"\u7d30\u80de\u662f\u751f\u7269\u7684\u57fa\u672c\u55ae\u4f4d\uff0c\u662f\u7531\u7d30\u80de\u7246\u3001\u7d30\u80de\u8cea\u3001\u57fa\u56e0\u9ad4\u548c\u6838\u91a3\u9ad4\u7d44\u6210\u7684\u3002\u7d30\u80de\u53ef\u4ee5\u5206\u70ba\u539f\u6838\u7d30\u80de\u548c\u771f\u6838\u7d30\u80de\u3002","permalink":"/learning/biology/structure-of-a-cell"},{"id":"learning/biology/water-acids-bases","title":"Water, Acids, Bases","description":"Water, Acids, Bases \u7684\u95dc\u4fc2\u662f\u4e00\u500b\u8907\u96dc\u7684\u7cfb\u7d71\uff0c\u53ef\u4ee5\u85c9\u7531\u6c34\u958b\u59cb\u4e86\u89e3\u3002\u6c34\u662f\u4e00\u7a2e polar molecule\uff0c\u6709\u5206\u6b63\u8ca0\u96fb\u8377\uff0c\u53ef\u4ee5\u548c\u96fb\u8377\u76f8\u95dc\u7684 molecule \u4e92\u76f8\u5438\u5f15\uff0c\u4f8b\u5982\u9e7d (NaCl)\u3002\u800c acids \u548c bases \u662f\u4e00\u7a2e\u96e2\u5b50\u7269\u8cea\uff0c\u53ef\u4ee5\u548c\u6c34\u4f5c\u7528\uff0cacids \u5177\u6709\u7d30\u83cc\u5206\u89e3\u7684\u80fd\u529b\uff0c\u800c bases \u5247\u662f\u53ef\u4ee5\u548c\u9178\u9084\u539f\u7684\u80fd\u529b\uff0c\u53ef\u4ee5\u7528\u4f86\u8abf\u6574 pH \u503c\u3002\u9019\u4e9b\u96e2\u5b50\u6703\u548c\u6c34\u5206\u5b50\u7522\u751f\u4e00\u500b\u4e09\u7dad\u7684\u6c34\u5305\u570d\u7d50\u69cb\uff0c\u6539\u8b8a\u6c34\u7684\u7279\u6027\uff0c\u4f8b\u5982\u6eb6\u89e3\u5ea6\u3001\u6297\u58d3\u529b\u7b49\u7b49\u3002","permalink":"/learning/biology/water-acids-bases"}]}')}}]);