"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[3890],{1130:i=>{i.exports=JSON.parse('{"label":"machine-learning","permalink":"/tags/machine-learning","allTagsPath":"/tags","count":17,"items":[{"id":"learning/machine-learning/improve-learning-algorithm/advice-for-applying-ml","title":"Advice for Applying ML","description":"\u5728\u6a5f\u5668\u5b78\u7fd2\u4e2d\uff0c\u8a55\u4f30\u5b78\u7fd2\u6f14\u7b97\u6cd5\u662f\u975e\u5e38\u91cd\u8981\u7684\u4e00\u500b\u6b65\u9a5f\u3002\u6211\u5011\u9700\u8981\u900f\u904e\u8abf\u6574\u6a21\u578b\u53c3\u6578\uff0c\u548c\u9069\u7576\u7684\u6e2c\u8a66\u65b9\u6cd5\u4f86\u78ba\u4fdd\u6a21\u578b\u8868\u73fe\u7684\u7a69\u5b9a\u6027\u3002\u672c\u6587\u5c07\u4ecb\u7d39\u5e7e\u500b\u5e38\u898b\u7684\u6a21\u578b\u8a55\u4f30\u65b9\u6cd5\uff0c\u4ee5\u53ca\u5c0d\u65bc\u907f\u514d overfitting \u7684\u65b9\u6cd5\uff0c\u4f8b\u5982 regularization \u548c cross validation\u3002","permalink":"/learning/machine-learning/improve-learning-algorithm/advice-for-applying-ml"},{"id":"learning/machine-learning/logistic-regression/classification-and-representation","title":"Classification and Representation","description":"Logistic regression \u662f\u4e00\u7a2e\u5206\u985e\u5668\uff0c\u5b83\u53ef\u4ee5\u9069\u7528\u65bc binary classification problem\uff0c\u4e5f\u5c31\u662f\u53ea\u6709\u5169\u7a2e\u7d50\u679c\u7684\u554f\u984c\u3002\u5b83\u4e5f\u53ef\u4ee5\u7528\u4f86\u8868\u793a\u4e00\u500b\u4e8b\u4ef6\u767c\u751f\u7684\u53ef\u80fd\u6027\uff0c\u6211\u5011\u53ef\u4ee5\u7528\u5b83\u4f86\u9810\u6e2c\u51fa\u7d50\u679c\u662f 0 \u6216 1 \u7684\u6a5f\u7387\u3002","permalink":"/learning/machine-learning/logistic-regression/classification-and-representation"},{"id":"learning/machine-learning/improve-learning-algorithm/improve-learning-algorithm","title":"Improve Learning Algorithm","description":"\u9019\u500b\u7ae0\u7bc0\u5c07\u4ecb\u7d39\u5982\u4f55\u8a2d\u8a08\u4e00\u500b\u6a5f\u5668\u5b78\u7fd2\u7cfb\u7d71\uff0c\u4ee5\u53ca\u5982\u4f55\u8a55\u4f30\u6f14\u7b97\u6cd5\u7684\u6e96\u78ba\u5ea6\u3001\u9632\u6b62 overfitting \u4ee5\u53ca\u5982\u4f55\u5229\u7528\u5927\u91cf\u8cc7\u6599\u4f86\u63d0\u5347\u6f14\u7b97\u6cd5\u7684\u6548\u80fd\u3002\u70ba\u4e86\u89e3\u91cb\u9019\u4e9b\u6982\u5ff5\uff0c\u6211\u5011\u5c07\u4ee5\u4e00\u500b\u5783\u573e\u90f5\u4ef6\u5206\u985e\u5668\u70ba\u4f8b\uff0c\u8a73\u7d30\u89e3\u8aaa\u5982\u4f55\u6536\u96c6\u8cc7\u6599\u3001\u64b0\u5beb algorithm \u4ee5\u53ca\u5982\u4f55\u8655\u7406\u504f\u659c\u8cc7\u6599\u3002","permalink":"/learning/machine-learning/improve-learning-algorithm/"},{"id":"learning/machine-learning/introduction/introduction","title":"Introduction","description":"\u6a5f\u5668\u5b78\u7fd2\u662f\u4e00\u9580\u6280\u8853\uff0c\u53ef\u4ee5\u8b93\u96fb\u8166\u81ea\u52d5\u5b78\u7fd2\u3001\u6539\u5584\u548c\u81ea\u6211\u6539\u9032\uff0c\u8b93\u96fb\u8166\u7372\u5f97\u77e5\u8b58\uff0c\u800c\u4e0d\u9700\u8981\u660e\u78ba\u7684\u7a0b\u5f0f\u6307\u793a\u3002\u5b83\u6d89\u53ca\u5230\u5169\u7a2e\u4e0d\u540c\u7684\u5b78\u7fd2\uff1a\u76e3\u7763\u5f0f\u5b78\u7fd2\u548c\u7121\u76e3\u7763\u5f0f\u5b78\u7fd2\u3002\u5728\u76e3\u7763\u5f0f\u5b78\u7fd2\u4e2d\uff0c\u6211\u5011\u6703\u5c07\u554f\u984c\u8207\u6b63\u78ba\u7b54\u6848\u4e00\u8d77\u63d0\u4f9b\u7d66\u7a0b\u5f0f\uff0c\u4ee5\u4fbf\u627e\u51fa\u5169\u8005\u4e4b\u9593\u7684\u95dc\u4fc2\u3002\u53ef\u4ee5\u5206\u70ba\u56de\u6b78\u554f\u984c\u548c\u5206\u985e\u554f\u984c\u3002\u800c\u5728\u7121\u76e3\u7763\u5b78\u7fd2\u4e2d\uff0c\u4e0d\u9700\u8981\u77e5\u9053\u8cc7\u6599\u4e2d\u6bcf\u500b\u554f\u984c\u5c0d\u61c9\u7684\u89e3\u7b54\u70ba\u4f55\uff0c\u800c\u662f\u7531\u7a0b\u5f0f\u5f9e\u8cc7\u6599\u4e2d\u627e\u51fa pattern \u548c\u7d50\u69cb\uff0c\u65b9\u6cd5\u901a\u5e38\u70ba\u5229\u7528\u8cc7\u6599\u4e2d\u7684\u8b8a\u6578\u95dc\u4fc2\u9032\u884c\u5206\u7fa4\u6f14\u7b97\u6cd5\uff08clustering algorithm\uff09\uff0c\u4ee5\u53ca Non-clustering algorithm\uff0c\u4e5f\u5c31\u662f Cocktail Party Algorithm\u3002","permalink":"/learning/machine-learning/introduction/"},{"id":"learning/machine-learning/neural-networks/introduction-of-neural-networks","title":"Introduction of Neural Networks","description":"\u795e\u7d93\u7db2\u8def\u662f\u70ba\u4e86\u6a21\u64ec\u4eba\u985e\u7684\u5927\u8166\u904b\u4f5c\uff0c\u5927\u8166\u53ef\u4ee5\u900f\u904e\u4e00\u7a2e learning algorithm \u505a\u5230\u807d\u3001\u8aaa\u3001\u8b80\u3001\u5beb\u7b49\u6d3b\u52d5\uff0c\u800c\u795e\u7d93\u7db2\u8def\u7684\u6700\u5c0f\u55ae\u4f4d\u70ba\u795e\u7d93\u5143\uff0c\u5b83\u53ef\u4ee5\u7531 input wires \u63a5\u6536\u591a\u500b\u8f38\u5165\uff0c\u4e26\u5c07\u8655\u7406\u904e\u7684\u8a0a\u606f\u50b3\u9001\u51fa\u53bb\u900f\u904e output wires\u3002\u6211\u5011\u53ef\u4ee5\u518d\u52a0\u4e0a bias unit \u4f5c\u70ba\u8f38\u5165\uff0c\u800c\u8655\u7406\u9019\u4e9b input \u7684\u70ba sigmoid (logistic) activation function\u3002\u7576\u6709\u591a\u5c64 layer \u6642\uff0c\u7b2c\u4e00\u5c64\u70ba input layer\uff0c\u6700\u5f8c\u4e00\u5c64\u70ba output layer\uff0c\u800c\u4e2d\u9593\u6240\u6709\u7684\u5c64\u7d71\u7a31\u70ba hidden layer\u3002\u6211\u5011\u7d66\u4e88 hidden layer \u7684 nodes \u4e00\u500b\u540d\u5b57\uff1aactivation units\uff0c\u5b83\u662f\u7d93\u7531 input \u548c matrix of weights \u4f5c\u7528 output \u904e\u4f86\u7684\uff0chypothesis \u5c31\u662f\u9019\u4e9b activation units \u7d93\u7531\u6b0a\u91cd\u77e9\u9663\u8207\u8f38\u5165\u77e9\u9663\u76f8\u4e58\u5f8c\u7684\u7d50\u679c\u3002","permalink":"/learning/machine-learning/neural-networks/introduction-of-neural-networks"},{"id":"learning/machine-learning/linear-regression/index","title":"Linear Regression","description":"\u9019\u500b\u7ae0\u7bc0\u6211\u5011\u6703\u4ecb\u7d39\u7dda\u6027\u56de\u6b78\uff0c\u7dda\u6027\u56de\u6b78\u662f\u4e00\u500b\u975e\u5e38\u7c21\u55ae\u7684\u6a5f\u5668\u5b78\u7fd2\u6a21\u578b\uff0c\u4f46\u662f\u5b83\u7684\u61c9\u7528\u537b\u975e\u5e38\u5ee3\u6cdb\uff0c\u4f8b\u5982\uff1a\u80a1\u7968\u9810\u6e2c\u3001\u623f\u50f9\u9810\u6e2c\u3001\u92b7\u552e\u9810\u6e2c\u7b49\u7b49\uff0c\u56e0\u6b64\u7dda\u6027\u56de\u6b78\u662f\u4e00\u500b\u975e\u5e38\u91cd\u8981\u7684\u6a5f\u5668\u5b78\u7fd2\u6a21\u578b\u3002","permalink":"/learning/machine-learning/linear-regression/"},{"id":"learning/machine-learning/logistic-regression/index","title":"Logistic Regression","description":"Classification \u662f\u4e00\u500b\u5c07\u554f\u984c\u5206\u6210\u5169\u500b\u985e\u5225\u6216\u4ee5\u4e0a\u7684\u554f\u984c\uff0c\u800c Logistic Regression \u662f\u4e00\u7a2e\u7528\u4f86\u89e3\u6c7a Binary Classification \u7684\u6a5f\u5668\u5b78\u7fd2\u6a21\u578b\u3002\u5b83\u4f7f\u7528 Sigmoid Function \u4f86\u9810\u6e2c Positive Class \u7684\u6a5f\u7387\uff0c\u4e26\u5c07 Decision Boundary \u7684\u9593\u9694\u503c\u8a2d\u5b9a\u70ba 0.5\uff0c\u4ee5\u4fbf\u66f4\u597d\u7684\u5206\u8fa8 0/1\u3002","permalink":"/learning/machine-learning/logistic-regression/"},{"id":"learning/machine-learning/logistic-regression/logistic-regression-model","title":"Logistic Regression Model","description":"Logistic Regression \u662f\u4e00\u7a2e\u975e\u5e38\u5e38\u7528\u7684 Machine Learning \u6f14\u7b97\u6cd5\uff0c\u4e3b\u8981\u7528\u65bc\u505a Classification \u7684\u4efb\u52d9\uff0c\u53ef\u4ee5\u628a\u8cc7\u6599\u8f49\u63db\u6210\u4e00\u500b\u6a5f\u7387\u3002\u8209\u500b\u4f8b\u5b50\uff0c\u5047\u8a2d\u6211\u5011\u8981\u9810\u6e2c\u4e00\u500b\u4eba\u662f\u5426\u6703\u60a3\u6709\u7279\u5b9a\u75be\u75c5\uff0c\u9019\u6642\u5019\u6211\u5011\u5c31\u53ef\u4ee5\u4f7f\u7528 Logistic Regression \u4f86\u628a\u9019\u500b\u554f\u984c\u8f49\u63db\u6210\u4e00\u500b\u6a5f\u7387\uff0c\u8868\u793a\u8a72\u500b\u4eba\u60a3\u6709\u8a72\u75be\u75c5\u7684\u53ef\u80fd\u6027\u6709\u591a\u9ad8\u3002","permalink":"/learning/machine-learning/logistic-regression/logistic-regression-model"},{"id":"learning/machine-learning/index","title":"Machine Learning","description":"\u9019\u500b\u7b46\u8a18\u5c0d\u61c9 Coursera \u8ab2\u7a0b Machine Learning\u3002 \u8ab2\u7a0b\u7531 Andrew Ng \u6559\u6388\u4e3b\u8b1b\uff0c\u662f\u4e00\u500b\u975e\u5e38\u597d\u7684\u6a5f\u5668\u5b78\u7fd2\u5165\u9580\u8ab2\u7a0b\u3002","permalink":"/learning/machine-learning/"},{"id":"learning/machine-learning/improve-learning-algorithm/ml-system-design","title":"ML System Design","description":"\u6211\u5011\u5c07\u5f9e\u5efa\u7acb\u4e00\u500b Spam Classifier \u4e86\u89e3\u8a2d\u8a08\u6a5f\u5668\u5b78\u7fd2\u7cfb\u7d71\u6240\u9700\u8981\u6ce8\u610f\u7684\u4e8b\u60c5\u3002\u9019\u500b Classifier \u5c07\u6703\u6559\u6211\u5011\u5982\u4f55\u6536\u96c6\u8cc7\u6599\u3001\u64b0\u5beb algorithm \u4e26\u5982\u4f55\u8655\u7406\u504f\u659c\u8cc7\u6599\uff0c\u4ee5\u53ca\u5982\u4f55\u5229\u7528 precision \u548c recall \u7684\u6982\u5ff5\u4f86\u8a55\u4f30\u6a5f\u5668\u5b78\u7fd2\u7cfb\u7d71\u7684\u6e96\u78ba\u5ea6\u3002\u6700\u5f8c\uff0c\u6211\u5011\u4e5f\u6703\u4e86\u89e3\u5230\u5982\u4f55\u5229\u7528\u5927\u91cf\u7684\u8cc7\u6599\u4f86\u63d0\u5347\u6f14\u7b97\u6cd5\u7684\u6e96\u78ba\u5ea6\u3002","permalink":"/learning/machine-learning/improve-learning-algorithm/ml-system-design"},{"id":"learning/machine-learning/linear-regression/model-and-cost-function","title":"Model and Cost Function","description":"Model \u8207 Cost Function \u4e4b\u9593\u6709\u8457\u975e\u5e38\u5bc6\u5207\u7684\u95dc\u4fc2\uff0c\u9019\u662f\u7531\u65bc Model \u662f\u7528\u4f86\u9810\u6e2c\u7684\u7d50\u679c\uff0c\u800c Cost Function \u5247\u662f\u7528\u4f86\u8a08\u7b97 Model \u9810\u6e2c\u7d50\u679c\u7684\u8aa4\u5dee\u3002\u56e0\u6b64\uff0cCost Function \u5c07\u6703\u7528\u65bc\u6700\u5c0f\u5316 Model \u4e2d\u51fa\u73fe\u7684\u8aa4\u5dee\uff0c\u8b93 Model \u7684\u9810\u6e2c\u7d50\u679c\u8d8a\u4f86\u8d8a\u7cbe\u78ba\u3002","permalink":"/learning/machine-learning/linear-regression/model-and-cost-function"},{"id":"learning/machine-learning/linear-regression/multivariate-linear-regression","title":"Multivariate Linear Regression","description":"Multivariate linear regression \u662f\u6307\u7576 linear progression \u6709\u591a\u500b features (variables) \u6642\uff0c\u5c31\u53ef\u4ee5\u7a31\u4f5c\u70ba multivariate linear regression\u3002\u8209\u4f8b\u4f86\u8aaa\uff0c\u53ef\u4ee5\u4f7f\u7528 size, ages, number of bedrooms, floors \u4f86\u5224\u65b7 house price\uff0c\u6b64\u6642\u6211\u5011\u53ef\u4ee5\u5c07 Hypothesis \u5beb\u6210\uff1a","permalink":"/learning/machine-learning/linear-regression/multivariate-linear-regression"},{"id":"learning/machine-learning/neural-networks/neural-network-training","title":"Neural Network Training","description":"\u795e\u7d93\u7db2\u8def\u5df2\u6210\u70ba\u89e3\u6c7a\u5f9e\u5716\u50cf\u8b58\u5225\u5230\u81ea\u7136\u8a9e\u8a00\u8655\u7406\u7b49\u5404\u7a2e\u554f\u984c\u7684\u91cd\u8981\u5de5\u5177\u3002 \u56e0\u6b64\uff0c\u7406\u89e3\u8207\u795e\u7d93\u7db2\u8def\u76f8\u95dc\u7684\u7b26\u865f (notation)\u3001\u6210\u672c\u51fd\u6578 (cost function) \u548c\u53cd\u5411\u50b3\u64ad\u7b97\u6cd5 (backpropagation algorithm) \u5c0d\u65bc\u6210\u529f\u8a13\u7df4\u548c\u4f7f\u7528\u81f3\u95dc\u91cd\u8981\u3002 \u5728\u672c\u6587\u6211\u5011\u9084\u5c07\u63a2\u7d22\u53cd\u5411\u50b3\u64ad\u7b97\u6cd5\u80cc\u5f8c\u7684\u6982\u5ff5\uff0c\u4ee5\u4fbf\u6211\u5011\u80fd\u5920\u66f4\u597d\u5730\u7406\u89e3\u7d50\u679c\u7684\u542b\u7fa9\u3002","permalink":"/learning/machine-learning/neural-networks/neural-network-training"},{"id":"learning/machine-learning/neural-networks/neural-networks","title":"Neural Networks","description":"\u795e\u7d93\u7db2\u8def\u662f\u4e00\u7a2e\u91cd\u8981\u7684\u6a5f\u5668\u5b78\u7fd2\u6280\u8853\uff0c\u53ef\u4ee5\u5f9e\u8655\u7406\u8907\u96dc\u7684\u8cc7\u6599\u5206\u6790\u554f\u984c\u5230\u8655\u7406\u81ea\u7136\u8a9e\u8a00\u7684\u554f\u984c\uff0c\u5b83\u7684\u57fa\u790e\u662f\u795e\u7d93\u5143 (neuron)\uff0c\u795e\u7d93\u5143\u662f\u4e00\u7a2e\u55ae\u5143\uff0c\u53ef\u4ee5\u63a5\u53d7\u591a\u500b\u8f38\u5165\uff0c\u4e26\u901a\u904e\u6fc0\u6d3b\u51fd\u6578 (activation function) \u7684\u4f5c\u7528\u7d66\u51fa\u8f38\u51fa\u3002\u795e\u7d93\u7db2\u8def\u53ef\u4ee5\u6709\u5f88\u591a\u5c64\uff0c\u6bcf\u5c64\u90fd\u6709\u8457\u4e0d\u540c\u7684\u6b0a\u91cd (weight) \u77e9\u9663\u4ee5\u53ca bias unit\uff0c\u5b83\u5011\u53ef\u4ee5\u4e00\u8d77\u7d44\u5408\u8d77\u4f86\u69cb\u6210\u4e00\u500b hypothesis \u4f86\u89e3\u6c7a\u554f\u984c\u3002\u8a13\u7df4\u795e\u7d93\u7db2\u8def\u9700\u8981\u4e86\u89e3\u795e\u7d93\u7db2\u8def\u7684\u7b26\u865f\u3001\u6210\u672c\u51fd\u6578\u8207\u53cd\u5411\u50b3\u64ad\u7b97\u6cd5\uff0c\u9019\u4e9b\u6280\u8853\u53ef\u4ee5\u5e6b\u52a9\u6211\u5011\u5c0d\u795e\u7d93\u7db2\u8def\u7684\u7d50\u679c\u6709\u66f4\u6df1\u5165\u7684\u4e86\u89e3\u3002","permalink":"/learning/machine-learning/neural-networks/"},{"id":"learning/machine-learning/linear-regression/normal-equation","title":"Normal Equation","description":"\u6709\u4e00\u7a2e\u65b9\u6cd5\u53ef\u4ee5\u4e0d\u7528\u900f\u904e\u8fed\u4ee3\u5c0b\u627e\u6700\u5c0f\u5316\u6210\u672c\u51fd\u6578 (cost function) \u7684\u53c3\u6578 (parameters)\uff0c\u9019\u7a2e\u65b9\u6cd5\u7a31\u70ba Normal equation\uff0c\u53ea\u8981\u5c07\u8a13\u7df4\u96c6 (training sets) \u7684\u7279\u5fb5 $x$ \u8207\u7d50\u679c $y$ \u8f49\u63db\u70ba\u77e9\u9663\uff0c\u5c31\u80fd\u5957\u7528 normal equation \u76f4\u63a5\u5f97\u5230\u6700\u4f73\u89e3\u3002","permalink":"/learning/machine-learning/linear-regression/normal-equation"},{"id":"learning/machine-learning/linear-regression/parameter-learning","title":"Parameter Learning","description":"Parameter learning \u662f\u6a5f\u5668\u5b78\u7fd2\u4e2d\u7684\u4e00\u7a2e\u6280\u8853\uff0c\u5b83\u662f\u4e00\u7a2e\u8a13\u7df4\u6a21\u578b\u7684\u65b9\u6cd5\uff0c\u901a\u5e38\u901a\u904e\u5c0d\u53c3\u6578\u9032\u884c\u8abf\u6574\u4f86\u6539\u5584\u6a21\u578b\u7684\u6027\u80fd\u3002\u53c3\u6578\u5b78\u7fd2\u901a\u5e38\u8207\u68af\u5ea6\u4e0b\u964d\u6cd5 (Gradient Descent) \u76f8\u95dc\uff0c\u56e0\u70ba\u68af\u5ea6\u4e0b\u964d\u6cd5\u662f\u66f4\u65b0\u53c3\u6578\u7684\u4e00\u7a2e\u65b9\u6cd5\uff0c\u901a\u904e\u4e0d\u65b7\u66f4\u65b0\u53c3\u6578\u4f86\u6539\u5584\u6a21\u578b\u6027\u80fd\u3002\u4f8b\u5982\uff0c\u53ef\u4ee5\u4f7f\u7528\u68af\u5ea6\u4e0b\u964d\u6cd5\u4f86\u66f4\u65b0\u7db2\u7d61\u4e2d\u7684\u53c3\u6578\uff0c\u4ee5\u6539\u5584\u7db2\u7d61\u7684\u6027\u80fd\u3002","permalink":"/learning/machine-learning/linear-regression/parameter-learning"},{"id":"learning/machine-learning/regularization/regularization","title":"Regularization","description":"\u6a5f\u5668\u5b78\u7fd2\u4e2d\uff0cOverfitting \u662f\u4e00\u500b\u5e38\u898b\u7684\u554f\u984c\u3002\u7576\u6211\u5011\u641c\u7d22 Hypothesis \u4f86\u64ec\u5408\u8a13\u7df4\u96c6\u6642\uff0c\u53ef\u80fd\u6703\u9047\u5230 Underfit\u3001Just right \u548c Overfit \u9019\u4e09\u7a2e\u60c5\u6cc1\u3002\u6211\u5011\u5c07 Overfitting \u7684\u5b9a\u7fa9\u70ba\uff1a\u592a\u591a\u7684 features \u4f7f\u5f97\u6c42\u5f97\u7684 Hypothesis \u5728 training data \u4e0a\u6709\u5f88\u597d\u7684\u5206\u6578\uff0c\u4f46\u537b\u4e0d\u80fd\u8b93 test set \u6216\u771f\u5be6\u6578\u64da\u4f7f\u7528\u3002","permalink":"/learning/machine-learning/regularization/"}]}')}}]);