"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[3716],{3613:e=>{e.exports=JSON.parse('{"label":"linear-regression","permalink":"/tags/linear-regression","allTagsPath":"/tags","count":5,"items":[{"id":"learning/machine-learning/linear-regression/index","title":"Linear Regression","description":"\u9019\u500b\u7ae0\u7bc0\u6211\u5011\u6703\u4ecb\u7d39\u7dda\u6027\u56de\u6b78\uff0c\u7dda\u6027\u56de\u6b78\u662f\u4e00\u500b\u975e\u5e38\u7c21\u55ae\u7684\u6a5f\u5668\u5b78\u7fd2\u6a21\u578b\uff0c\u4f46\u662f\u5b83\u7684\u61c9\u7528\u537b\u975e\u5e38\u5ee3\u6cdb\uff0c\u4f8b\u5982\uff1a\u80a1\u7968\u9810\u6e2c\u3001\u623f\u50f9\u9810\u6e2c\u3001\u92b7\u552e\u9810\u6e2c\u7b49\u7b49\uff0c\u56e0\u6b64\u7dda\u6027\u56de\u6b78\u662f\u4e00\u500b\u975e\u5e38\u91cd\u8981\u7684\u6a5f\u5668\u5b78\u7fd2\u6a21\u578b\u3002","permalink":"/learning/machine-learning/linear-regression/"},{"id":"learning/machine-learning/linear-regression/model-and-cost-function","title":"Model and Cost Function","description":"Model \u8207 Cost Function \u4e4b\u9593\u6709\u8457\u975e\u5e38\u5bc6\u5207\u7684\u95dc\u4fc2\uff0c\u9019\u662f\u7531\u65bc Model \u662f\u7528\u4f86\u9810\u6e2c\u7684\u7d50\u679c\uff0c\u800c Cost Function \u5247\u662f\u7528\u4f86\u8a08\u7b97 Model \u9810\u6e2c\u7d50\u679c\u7684\u8aa4\u5dee\u3002\u56e0\u6b64\uff0cCost Function \u5c07\u6703\u7528\u65bc\u6700\u5c0f\u5316 Model \u4e2d\u51fa\u73fe\u7684\u8aa4\u5dee\uff0c\u8b93 Model \u7684\u9810\u6e2c\u7d50\u679c\u8d8a\u4f86\u8d8a\u7cbe\u78ba\u3002","permalink":"/learning/machine-learning/linear-regression/model-and-cost-function"},{"id":"learning/machine-learning/linear-regression/multivariate-linear-regression","title":"Multivariate Linear Regression","description":"Multivariate linear regression \u662f\u6307\u7576 linear progression \u6709\u591a\u500b features (variables) \u6642\uff0c\u5c31\u53ef\u4ee5\u7a31\u4f5c\u70ba multivariate linear regression\u3002\u8209\u4f8b\u4f86\u8aaa\uff0c\u53ef\u4ee5\u4f7f\u7528 size, ages, number of bedrooms, floors \u4f86\u5224\u65b7 house price\uff0c\u6b64\u6642\u6211\u5011\u53ef\u4ee5\u5c07 Hypothesis \u5beb\u6210\uff1a","permalink":"/learning/machine-learning/linear-regression/multivariate-linear-regression"},{"id":"learning/machine-learning/linear-regression/normal-equation","title":"Normal Equation","description":"\u6709\u4e00\u7a2e\u65b9\u6cd5\u53ef\u4ee5\u4e0d\u7528\u900f\u904e\u8fed\u4ee3\u5c0b\u627e\u6700\u5c0f\u5316\u6210\u672c\u51fd\u6578 (cost function) \u7684\u53c3\u6578 (parameters)\uff0c\u9019\u7a2e\u65b9\u6cd5\u7a31\u70ba Normal equation\uff0c\u53ea\u8981\u5c07\u8a13\u7df4\u96c6 (training sets) \u7684\u7279\u5fb5 $x$ \u8207\u7d50\u679c $y$ \u8f49\u63db\u70ba\u77e9\u9663\uff0c\u5c31\u80fd\u5957\u7528 normal equation \u76f4\u63a5\u5f97\u5230\u6700\u4f73\u89e3\u3002","permalink":"/learning/machine-learning/linear-regression/normal-equation"},{"id":"learning/machine-learning/linear-regression/parameter-learning","title":"Parameter Learning","description":"Parameter learning \u662f\u6a5f\u5668\u5b78\u7fd2\u4e2d\u7684\u4e00\u7a2e\u6280\u8853\uff0c\u5b83\u662f\u4e00\u7a2e\u8a13\u7df4\u6a21\u578b\u7684\u65b9\u6cd5\uff0c\u901a\u5e38\u901a\u904e\u5c0d\u53c3\u6578\u9032\u884c\u8abf\u6574\u4f86\u6539\u5584\u6a21\u578b\u7684\u6027\u80fd\u3002\u53c3\u6578\u5b78\u7fd2\u901a\u5e38\u8207\u68af\u5ea6\u4e0b\u964d\u6cd5 (Gradient Descent) \u76f8\u95dc\uff0c\u56e0\u70ba\u68af\u5ea6\u4e0b\u964d\u6cd5\u662f\u66f4\u65b0\u53c3\u6578\u7684\u4e00\u7a2e\u65b9\u6cd5\uff0c\u901a\u904e\u4e0d\u65b7\u66f4\u65b0\u53c3\u6578\u4f86\u6539\u5584\u6a21\u578b\u6027\u80fd\u3002\u4f8b\u5982\uff0c\u53ef\u4ee5\u4f7f\u7528\u68af\u5ea6\u4e0b\u964d\u6cd5\u4f86\u66f4\u65b0\u7db2\u7d61\u4e2d\u7684\u53c3\u6578\uff0c\u4ee5\u6539\u5584\u7db2\u7d61\u7684\u6027\u80fd\u3002","permalink":"/learning/machine-learning/linear-regression/parameter-learning"}]}')}}]);